mapper.select:
select carInfoNo, carName, carFuel, carCapacity , carTypes, filename, orgFileName, filesize, realsavepath from(
select rownum rnum, carInfoNo, carName, carFuel, carCapacity , carTypes, filename, orgFileName, filesize, realsavepath from( select cbi.carInfoNo, cbi.carName, cbi.carFuel, cbi.carCapacity , cbi.carTypes, cfup.filename,cfup.orgFileName, cfup.filesize, cfup.realsavepath from carbasicinfo cbi, car_fileupload cfup where
cbi.carinfoNo = cfup.carinfoNo order by cbi.carinfoNo desc ) )where rnum between #{startRow} and #{endRow}

mapper.update:
update 
	carBasicInfo
set
	 carName = #{carName},carTypes = #{carTypes}, carCapacity = #{carCapacity}, carFuel = #{carFuel}
where carInfoNo = #{carInfoNo}
